import React, { Component } from 'react';
import { ethers } from 'ethers';
import NFTManagerABI from './NFTManagerABI.json';

const initData = {
    pre_heading: "Auctions",
    heading: "Live Auctions",
    btnText: "View All"
}

class AuctionsOne extends Component {
    state = {
        initData: {},
        nftData: null
    }

    async componentDidMount() {
        this.setState({ initData: initData });

        // Initialize ethers with Infura
        const provider = new ethers.providers.InfuraProvider("mainnet", "b2e950fb6dfb4ece9d7b84ea0cce4c93");
        
        // Set the contract address and instantiate the contract
        const contractAddress = "0x6960Dbc55282Ac60da31277E3233c8f3c2b6c764";
        const nftManager = new ethers.Contract(contractAddress, NFTManagerABI, provider);

        try {
            // Fetch the token URI for the NFT with ID 1
            const tokenId = 1;
            const tokenURI = await nftManager.tokenURI(tokenId);

            // If the tokenURI is an IPFS link, construct the full IPFS URL
            const img = tokenURI.startsWith("ipfs://") 
                ? `https://ipfs.io/ipfs/${tokenURI.slice(7)}` 
                : tokenURI;

            // Set the fetched data to state
            const nftData = {
                id: "1",
                img: img,
                date: "2021-12-09",
                title: "Virtual Worlds",
                seller_thumb: "/img/avatar_1.jpg",
                seller: "@Richard",
                price: "1.5 BNB",
                count: "1 of 1"
            };

            this.setState({ nftData: nftData });
        } catch (error) {
            console.error("Error fetching NFT data:", error);
        }
    }

    render() {
        const { nftData } = this.state;

        return (
            <section className="live-auctions-area">
                <div className="container">
                    <div className="row">
                        <div className="col-12">
                            {/* Intro */}
                            <div className="intro d-flex justify-content-between align-items-end m-0">
                                <div className="intro-content">
                                    <span>{this.state.initData.pre_heading}</span>
                                    <h3 className="mt-3 mb-0">{this.state.initData.heading}</h3>
                                </div>
                                <div className="intro-btn">
                                    <a className="btn content-btn" href="/auctions">{this.state.initData.btnText}</a>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="auctions-slides">
                        <div className="swiper-container slider-mid items">
                            <div className="swiper-wrapper">
                                {/* Display NFT Data */}
                                {nftData && (
                                    <div key={`auc_${nftData.id}`} className="swiper-slide item">
                                        <div className="card">
                                            <div className="image-over">
                                                <a href="/item-details">
                                                    <img className="card-img-top" src={nftData.img} alt={nftData.title} />
                                                </a>
                                            </div>
                                            {/* Card Caption */}
                                            <div className="card-caption col-12 p-0">
                                                {/* Card Body */}
                                                <div className="card-body">
                                                    <div className="countdown-times mb-3">
                                                        <div className="countdown d-flex justify-content-center" data-date={nftData.date} />
                                                    </div>
                                                    <a href="/item-details">
                                                        <h5 className="mb-0">{nftData.title}</h5>
                                                    </a>
                                                    <a className="seller d-flex align-items-center my-3" href="/item-details">
                                                        <img className="avatar-sm rounded-circle" src={nftData.seller_thumb} alt={nftData.seller} />
                                                        <span className="ml-2">{nftData.seller}</span>
                                                    </a>
                                                    <div className="card-bottom d-flex justify-content-between">
                                                        <span>{nftData.price}</span>
                                                        <span>{nftData.count}</span>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                )}
                            </div>
                            <div className="swiper-pagination" />
                        </div>
                    </div>
                </div>
            </section>
        );
    }
}

export default AuctionsOne;
